import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
function App() {

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddCardPopupOpen, setIsAddCardPopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isCardPopupOpen, setIsCardPopupOpen] = React.useState(false);

  function handleEditProfilePopupClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlacePopupClick() {
    setIsAddCardPopupOpen(true);
  }

  function handleEditAvatarPopupClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleCardPopupClick() {
    setIsCardPopupOpen(true);
  }

  function closePopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsCardPopupOpen(false);
  }

  return (
    <div className="page-container">

      <Header />
      <Main
        onEditProfileClick={ handleEditProfilePopupClick }
      />

      <Footer />
      <PopupWithForm name='edit-profile' isOpen={isEditProfilePopupOpen} onClose={closePopups} headerText="Edit profile" >
        <input className="popup-menu__input popup-menu__input_type_name" name='name' type='text' placeholder='Full Name' />
        <input className="popup-menu__input popup-menu__input_type_title" name='title' type='text' placeholder='Title' />
      </PopupWithForm>

      <PopupWithForm name='add-card' isOpen={isAddCardPopupOpen} onClose={closePopups} headerText="New Place" >
        <input className="popup-menu__input popup-menu__input_type_title" name='userName' type='text' placeholder='Name' />
        <input className="popup-menu__input popup-menu__input_type_url" name='link' type='text' placeholder='Image link' />
      </PopupWithForm>
      {/* <PopupWithForm /> */}
      {/* <PopupWithForm /> */}


      <div className="popup-menu" id="popup-menu_type_add-card">
        <div className="popup-menu__edit-form">
          <button className="popup-menu__close-button" type="button" aria-label="close-button"></button>
          <h3 className="popup-menu__title">New Place</h3>
          <form className="popup-menu__submit-form" name="form-wrapper" id="add-card-form">
            <input id="input_type_card_name" type="text" placeholder="Title" name="name"
              className="popup-menu__input popup-menu__input_type_title" minLength="1" maxLength="30" required />
            <span id="input_type_card_name-error" className="popup-menu__error"></span>
            <input id="input_type_card_url" type="url" placeholder="Image link" name="link"
              className="popup-menu__input popup-menu__input_type_url" required />
            <span id="input_type_card_url-error" className="popup-menu__error"></span>
            <button className="popup-menu__save-button" id="add-card_save-button" type="submit">Create</button>
          </form>
        </div>
      </div>
      <div className="popup-menu" id="popup-menu_image">
        <div className="popup-menu__image-container">
          <div className="popup-menu__image-box">
            <button className="popup-menu__close-button" id="image_close_button" type="button"
              aria-label="close-button"></button>
            <img className="popup-menu__image" src="#" alt="description" />
            <div className="popup-menu__text"></div>
          </div>
        </div>
      </div>
      <div className="popup-menu" id="popup-menu_del-confirm">
        <form className="popup-menu__del-confirm-container">
          <button className="popup-menu__close-button" type="button" aria-label="close-button"></button>
          <h3 className="popup-menu__title popup-menu__title_delConfirm">Are you sure?</h3>
          <button className="popup-menu__save-button" id="delete-confirm_button" type="submit">Yes</button>
        </form>
      </div>
      <div className="popup-menu" id="popup-menu_type_change-profilePic">
        <div className="popup-menu__edit-form">
          <button className="popup-menu__close-button" type="button" aria-label="close-button"></button>
          <h3 className="popup-menu__title">Change Profile Picture</h3>
          <form className="popup-menu__submit-form" name="form-wrapper" id="change-pic-form">
            <input id="input_type_card_pic_url" type="url" placeholder="Profile Image link" name="link"
              className="popup-menu__input popup-menu__input_type_url" required />
            <span id="input_type_card_pic_url-error" className="popup-menu__error"></span>
            <button className="popup-menu__save-button" id="change-avatar_button" type="submit">Save</button>
          </form>
        </div>
      </div>

      <template id="card-template">
        <li className="card">
          <button className="card__erase" type="button" aria-label="erase-button"></button>
          <img className="card__image" src="#" alt="" />
          <div className="card__photo-description">
            <h2 className="card__text"></h2>
            <div className="card__heart-container">
              <button className="card__like-button" type="button" aria-label="like-button"></button>
              <span className="card__like-count"></span>
            </div>

          </div>
        </li>
      </template>
    </div>
  );

}
export default App;
