import { withRouter ,useHistory } from 'react-router-dom';

function Register(props) {

    const [userName, setUserName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [message, setMessage] = useState('');
    const history = useHistory();

    const handleSubmit = (evt) => {
        evt.preventDefault();
        if (password !== confirmPassword) {
            auth.register(username, password, email).then((res) => {
                if (res) {
                    setMessage('');
                    history.push('/login');
                }
                setMessage('Something went wrong, Please try again.')
            })
         }
    }





    return (
    <div className="authorisation">
      <form className="authorisation__form" name="sign-up">
        <h3 className="authorisation__title">Sign Up</h3>
        <input
          type="email"
          className="authorisation__input"
          name="email"
          id="sign-up-email-input"
          placeholder="Email"
          required
        />
        <input
          type="password"
          className="authorisation__input"
          name="password"
          id="sign-up-password-input"
          placeholder="Password"
          required
        />
        <button
          type="submit"
          className="authorisation__button"
        >
          Sign Up
        </button>
      </form>
      <p className="authorisation__underline">
        Already a member? Log in here!
      </p>
    </div>
  );
}

export default Register;
