import React from 'react';
import { api } from '../utils/api';
import Card from './Card';
import CurrentUserContext from '../contexts/CurrentUserContext';

function Main(props) {
  const [cards, setCards] = React.useState([]);
  const currentUser = React.useContext(CurrentUserContext);

  React.useEffect(() => {
    api
      .getInitialCards()
      .then((data) => {
        setCards(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  function handleCardLike(card) {
    // Check one more time if this card was already liked
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    // Send a request to the API and getting the updated card data
    if (!isLiked) {
      api.like(card._id).then((newCard) => {
        setCards((state) =>
          state.map((c) => (c._id === card._id ? newCard : c))
        );
      });
    } else {
      api.dislike(card._id).then((newCard) => {
        setCards((state) =>
          state.map((c) => (c._id === card._id ? newCard : c))
        );
      });
    }
  }

  return (
    <main className="main">
      <section className="profile-menu">
        <div className="profile-menu__profile-pic">
          <img
            className="profile-menu__avatar"
            src={currentUser.avatar}
            id="avatar_image"
            alt="avatar image"
          ></img>
          <button
            onClick={props.onEditAvatarClick}
            className="profile-menu__avatar-button"
            type="button"
          ></button>
        </div>
        <div className="profile-menu__info">
          <div className="profile-menu__alignment">
            <h1 className="profile-menu__full-name">{currentUser.name}</h1>
            <button
              onClick={props.onEditProfileClick}
              className="profile-menu__edit-button"
              type="button"
              aria-label="edit-button"
            ></button>
          </div>
          <p className="profile-menu__title">{currentUser.about}</p>
        </div>
        <button
          onClick={props.onAddPlaceClick}
          className="profile-menu__add-button"
          type="button"
          aria-label="add-button"
        ></button>
      </section>

      <section className="cards">
        <ul className="cards__list">
          {cards.map((element) => (
            <Card
              key={element._id}
              card={element}
              onClick={props.onCardImageClick}
              updateCardData={props.updateCardData}
              onCardLike={handleCardLike}
            />
          ))}
        </ul>
      </section>
    </main>
  );
}

export default Main;
