function Main() {
    return(
        <main className="main">
        <section className="profile-menu">
          <div className="profile-menu__profile-pic">
            <img className="profile-menu__avatar" id="avatar_image" alt=""></img>
            <button className="profile-menu__avatar-button" type="button"></button>
          </div>
          <div className="profile-menu__info">
            <div className="profile-menu__alignment">
              <h1 className="profile-menu__full-name"></h1>
              <button className="profile-menu__edit-button" type="button" aria-label="edit-button"></button>
            </div>
            <p className="profile-menu__title"></p>
          </div>
          <button className="profile-menu__add-button" type="button" aria-label="add-button"></button>
        </section>

        <section className="cards">
          <ul className="cards__list"></ul>
        </section>
      </main>
    )

    function handleAddPlaceClick() {
        document.querySelector('.profile-menu__add-button').addEventListener('click', () => {
            document.querySelector('#popup-menu_type_add-card').classlist.add('popup-menu_is-opened');
            console.log('can you see me?')
        });
    }
    handleAddPlaceClick();
    function handleEditAvatarClick() {
        console.log('Edit avatar clicked');
    }

    function handleEditProfileClick() {
        console.log('Edit profile clicked');
    }
}




export default Main;
