function PopupWithForm(props) {
    return (
        <div className={`popup-menu ${ isOpened ? 'popup-menu_opened' : '' }`} id={`popup-menu_type_${props.name}`}>
        <div className="popup-menu__edit-form">
          <button onClick={ props.onClose } className="popup-menu__close-button" type="button" aria-label="close-button"></button>
                <h3 className="popup-menu__title">{ props.title }</h3>
          <form className="popup-menu__submit-form" name={props.name} id="profile-form">
                { props.children }
                    <button onClick={ props.buttonText } className="popup-menu__save-button" id="edit-profile_save-button" type="submit"></button>
          </form>
        </div>
      </div>
    )
}

