import logo from '../images/logo.svg';
import { Link } from 'react-router-dom';

function Header(props) {
  const hamburgerIcon = <svg
  className="nav-hamburger-icon"
  viewBox="0 0 100 80"
  width="25"
  height="25"
  fill="white"
>
  <rect width="100" height="15"></rect>
  <rect y="30" width="100" height="15"></rect>
  <rect y="60" width="100" height="15"></rect>
</svg>

  function renderMainHeader() {
    return (
      <header className="header">
        <img className="logo" src={logo} id="logo-icon" alt="logo"></img>
        <button className="header_hamburger-menu"></button>
        <div className="header__user-container">
          <div className="header__user">{ props.email }</div>
          <button className="header__logout" onClick={props.handleLogout}>Log out</button>
        </div>
      </header>
    );
  }

  function renderRegisterHeader() {
    return (
      <header className="header">
        <img className="logo" src={logo} id="logo-icon" alt="logo"></img>
        <Link className="header__link-btn" to={"/login"}>Log In</Link>
      </header>
    );
  }

  function renderLoginHeader() {
    return (
      <header className="header">
        <img className="logo" src={logo} id="logo-icon" alt="logo"></img>
        <Link className="header__link-btn" to={"/signup"}>Sign Up</Link>
      </header>
    );
  }

  if (props.type === 'login') {
    return renderLoginHeader();
  }

  if (props.type === 'signup') {
    return renderRegisterHeader();
  }

  return renderMainHeader();
}

export default Header;
